import React, { useEffect, useMemo } from "react";
import "./connected.css";

const Connected = ({
  account,
  candidates,
  citizenData,
  remainingTime,
  number,
  handleNumberChange,
  voteFunction,
  showButton,
  onWalletAddressChange,
  isLoading // Add this prop
}) => {
  useEffect(() => {
    const handleAccountsChanged = (accounts) => {
      if (accounts.length === 0 || accounts[0] !== account) {
        onWalletAddressChange();
      }
    };

    if (window.ethereum) {
      window.ethereum.on("accountsChanged", handleAccountsChanged);
    }

    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener("accountsChanged", handleAccountsChanged);
      }
    };
  }, [account, onWalletAddressChange]);

  const filteredCandidates = useMemo(() => {
    if (!citizenData?.area || !candidates) return [];
    return candidates.filter(candidate => candidate.area === citizenData.area);
  }, [candidates, citizenData?.area]);

  if (isLoading) {
    return <div className="connected-container">Loading blockchain data...</div>;
  }

  return (
    <div className="connected-container">
      <h1 className="connected-header">You are Connected to Metamask</h1>
      <p className="connected-account">Metamask Account: {account}</p>
      <p className="connected-account">Remaining Time: {Math.floor(remainingTime / 60)} minutes</p>

      {showButton ? (
        <p className="connected-account">You have already voted</p>
      ) : (
        <div>
          <input
            type="number"
            placeholder="Enter Candidate Index"
            value={number}
            onChange={handleNumberChange}
            className="candidate-input"
          />
          <button className="login-button" onClick={voteFunction}>Vote</button>
        </div>
      )}

      <div className="table-container">
      <table className="candidates-table">
          <thead>
            <tr>
              <th>Index</th>
              <th>Candidate Name</th>
              <th>Candidate Votes</th>
              <th>Party Name</th>
              <th>Area</th>
            </tr>
          </thead>
          <tbody className="scrollable-body">
            {filteredCandidates.length > 0 ? (
              filteredCandidates.map((candidate) => (
                <tr key={candidate.index}>
                  <td>{candidate.index}</td>
                  <td>{candidate.name}</td>
                  <td>{candidate.voteCount}</td>
                  <td>{candidate.party}</td>
                  <td>{candidate.area}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="5">
                  {!citizenData?.area 
                    ? "Please verify your citizenship to see available candidates" 
                    : "No candidates available for your area"}
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Connected;
