import React, { useState } from 'react';
import axios from 'axios';
import { Button, Form, Container, Header, Message, Table, Dropdown } from 'semantic-ui-react';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Database() {
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [age, setAge] = useState("");
    const [adharNumber, setAdharNumber] = useState("");
    const [area, setArea] = useState("");
    const [fingerprint, setFingerprint] = useState(null); // State for fingerprint
    const [submissionSuccess, setSubmissionSuccess] = useState(false);
    const [submissionError, setSubmissionError] = useState(null); // State for submission error
    const [users, setUsers] = useState([]);
    const [showUsers, setShowUsers] = useState(false);

    // Options for area dropdown
    const areaOptions = [
        { key: 'a', text: 'Area 1', value: 'Area 1' },
        { key: 'b', text: 'Area 2', value: 'Area 2' },
        { key: 'c', text: 'Area 3', value: 'Area 3' }
    ];

    // Helper functions for HTTP calls
async function PostMFS100Client(method, jsonData) {
    try {
        const response = await fetch("https:localhost:3000/mfs100/" + method, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: jsonData,
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        return await response.json();
    } catch (error) {
        console.error('Error:', error);
        return { httpStatus: false, err: error.message };
    }
}

async function GetMFS100Client(method) {
    try {
        const response = await fetch("https:localhost:3000/mfs100/" + method, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        });
        if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
        return await response.json();
    } catch (error) {
        console.error('Error:', error);
        return { httpStatus: false, err: error.message };
    }
}

// Helper Classes
class Biometric {
    constructor(BioType, BiometricData, Pos, Nfiq, Na) {
        this.BioType = BioType;
        this.BiometricData = BiometricData;
        this.Pos = Pos;
        this.Nfiq = Nfiq;
        this.Na = Na;
    }
}

class MFS100Request {
    constructor(BiometricArray) {
        this.Biometrics = BiometricArray;
    }
}
    // Function to capture fingerprint
    // const captureFingerprint = async () => {
    //     try {
    //         const response = await axios.post('http://localhost:4000/capture-fingerprint');
    //         setFingerprint(response.data.fingerprint); // Store captured fingerprint

    //         // Show success toast message
    //         toast.success("Fingerprint captured successfully!");
    //     } catch (error) {
    //         console.error('Error capturing fingerprint:', error);

    //         // Show error toast message
    //         toast.error("Failed to capture fingerprint. Please try again!");
    //     }
    // };

    // const collectData = async (e) => {
    //     e.preventDefault();

    //     try {
    //         const result = await axios.post('http://localhost:4000/', {
    //             firstName, lastName, age, adharNumber, area, fingerprint // Include fingerprint and area in the data
    //         });
    //         localStorage.setItem("user", JSON.stringify(result.data));
    //         setSubmissionSuccess(true);
    //         setSubmissionError(null); // Clear error message on successful submission
    //     } catch (error) {
    //         console.error('Error submitting data:', error);
    //         setSubmissionError('Error submitting data. Please try again.');
    //     }
    // };

    async function CaptureFinger(quality, timeout) {
        const MFS100Request = {
            "Quality": quality,
            "TimeOut": timeout,
        };
        const jsondata = JSON.stringify(MFS100Request);
        return await PostMFS100Client("capture", jsondata);
    }

    const collectData = async (e) => {
        e.preventDefault();

        try {
            // Capture fingerprint
            const fingerprintResponse = await CaptureFinger(80, 5000);

            if (!fingerprintResponse.httpStatus) {
                throw new Error(fingerprintResponse.err); // Handle fingerprint capture error
            }

            const fingerprint = fingerprintResponse.BiometricData; // Adjust according to the actual response structure

            // Now, send the citizen data along with the fingerprint
            const result = await axios.post('http://localhost:4000/', {
                firstName,
                lastName,
                age,
                adharNumber,
                area,
                fingerprint // Include the captured fingerprint
            });

            localStorage.setItem("user", JSON.stringify(result.data));
            setSubmissionSuccess(true);
            setSubmissionError(null); // Clear error message on successful submission
        } catch (error) {
            console.error('Error submitting data:', error);
            setSubmissionError('Error submitting data. Please try again.');
        }
    };


    const fetchUsers = async () => {
        try {
            const response = await axios.get('http://localhost:4000/users');
            setUsers(response.data);
            setShowUsers(true);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    return (
        <Container text>
            <ToastContainer /> {/* Add ToastContainer here */}

            <Button
                secondary
                onClick={fetchUsers}
                style={{
                    position: 'absolute',
                    top: '20px',
                    right: '20px',
                    zIndex: 1000,
                }}
            >
                Fetch Users
            </Button>
            <Header as='h2' textAlign='center' style={{ margin: '20px 0' }}>
                User Registration
            </Header>
            {submissionSuccess && (
                <Message success header='Submission Successful' content="Your data has been captured!" />
            )}
            {submissionError && (
                <Message error header='Submission Error' content={submissionError} /> 
            )}
            <Form onSubmit={collectData} size='large'>
                <Form.Field>
                    <label>First Name</label>
                    <input
                        placeholder='Enter your first name'
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                    />
                </Form.Field>
                <Form.Field>
                    <label>Last Name</label>
                    <input
                        placeholder='Enter your last name'
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                    />
                </Form.Field>
                <Form.Field>
                    <label>Age</label>
                    <input
                        placeholder='Enter your age'
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                    />
                </Form.Field>
                <Form.Field>
                    <label>Adhar Number</label>
                    <input
                        placeholder='Enter your Adhar number'
                        value={adharNumber}
                        onChange={(e) => setAdharNumber(e.target.value)}
                    />
                </Form.Field>
                <Form.Field>
                    <label>Select Your Area</label>
                    <Dropdown
                        placeholder='Select Area'
                        fluid
                        selection
                        options={areaOptions}
                        onChange={(e, { value }) => setArea(value)} // Set area state on selection
                    />
                </Form.Field>

                {/* Fingerprint Capture Button */}
                <Button type='button' onClick={CaptureFinger(80, 5000)} style={{ marginTop: '10px', borderRadius: '20px' }}>
                    Capture Fingerprint
                </Button>

                <Button primary type='submit' style={{ marginTop: '10px', borderRadius: '20px' }}>
                    Submit
                </Button>
            </Form>

            {showUsers && users.length > 0 && (
                <Table celled style={{ marginTop: '20px' }}>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>First Name</Table.HeaderCell>
                            <Table.HeaderCell>Last Name</Table.HeaderCell>
                            <Table.HeaderCell>Age</Table.HeaderCell>
                            <Table.HeaderCell>Adhar Number</Table.HeaderCell>
                            <Table.HeaderCell>Area</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {users.map((user) => (
                            <Table.Row key={user._id}>
                                <Table.Cell>{user.firstName}</Table.Cell>
                                <Table.Cell>{user.lastName}</Table.Cell>
                                <Table.Cell>{user.age}</Table.Cell>
                                <Table.Cell>{user.adharNumber}</Table.Cell>
                                <Table.Cell>{user.area}</Table.Cell>
                            </Table.Row>
                        ))}
                    </Table.Body>
                </Table>
            )}
        </Container>
    );
}

export default Database;
